import React, { useState } from "react";
import axios from "axios";

function SignUpDetails() {
  const [signUpData, setSignUpData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [error, setError] = useState("");

  const handleSignUp = () => {
    axios
      .post("http://localhost:4000/signup", signUpData)
      .then((response) => {
        const { message } = response.data;
        console.log(message);
        setErro
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data &&
          error.response.data.error
        ) {
          setError(error.response.data.error);
        } else {
          setError("An error occurred while creating the user");
        }
        console.log(error);
      });
  };

  return (
    <div style={{ height: "100%", width: "100%" }}>
      <div style={{ display: "flex", height: "100%", width: "100%" }}>
        <div
          style={{
            height: "100%",
            width: "40%",
            textAlign: "right",
            paddingRight: "5vw",
            paddingLeft: "1vw",
            fontWeight: "600",
            color: "#474444",
            fontSize: "1.3em",
            paddingBottom: "3vw",
          }}
        >
          <p style={{ height: "22%", marginTop: "3vh" }}>Name</p>
          <p style={{ height: "22%" }}>Email</p>
          <p style={{ height: "22%" }}>Password</p>
          <p style={{ height: "22%" }}>Confirm Password</p>
        </div>
        <div style={{ height: "100%", width: "60%" }}>
          <form
            method="post"
            style={{ marginTop: "-1vh", paddingBottom: "3vw" }}
          >
            <p>
              <input
                className="signup-login-input"
                onChange={(e) =>
                  setSignUpData({ ...signUpData, name: e.target.value })
                }
              ></input>
            </p>
            <p>
              <input
                className="signup-login-input"
                onChange={(e) =>
                  setSignUpData({ ...signUpData, email: e.target.value })
                }
              ></input>
            </p>
            <p>
              <input
                className="signup-login-input"
                onChange={(e) =>
                  setSignUpData({ ...signUpData, password: e.target.value })
                }
              ></input>
            </p>
            <p>
              <input
                className="signup-login-input"
                onChange={(e) =>
                  setSignUpData({
                    ...signUpData,
                    confirmPassword: e.target.value,
                  })
                }
              ></input>
            </p>
          </form>
        </div>
      </div>
      <div
        style={{
          height: "15%",
          width: "100%",
          marginBottom: "2vh",
          marginTop: "1vh",
        }}
      >
        <button
          onClick={handleSignUp}
          style={{
            marginLeft: "35vw",
            marginRight: "35vw",
            backgroundColor: "#A9BCFF",
            height: "90%",
            width: "22%",
            borderRadius: "8px",
            border: "none",
            fontWeight: "600",
            fontSize: "1.3em",
            color: "#474444",
          }}
        >
          Sign-Up
        </button>
      </div>
    </div>
  );
}

export default SignUpDetails;
