import React, { useState } from "react";
import plusicon from "../images/plusicon.png";
import EachQuestion from "./EachQuestion";

function AddQuestionsPopup({
  setIsAddQuestionsPopupOpen,
  setIsQuizPublishedPopupOpen,
}) {
  const [questions, setQuestions] = useState([{ id: 1 }]);

  const handleAddQuestion = () => {
    if (questions.length < 5) {
      setQuestions((prevQuestions) => [
        ...prevQuestions,
        { id: prevQuestions.length + 1 },
      ]);
    }
  };

  return (
    <div
      style={{
        paddingBottom: "7vh",
        paddingTop: "4vh",
        paddingRight: "2.5vw",
        paddingLeft: "2.5vw",
        marginLeft: "3vw",
        backgroundColor: "white",
        borderRadius: "10px",
        width: "50vw",
      }}
    >
      <div
        style={{
          height: "10vh",
          width: "50vw",
          display: "flex",
          justifyContent: "space-between",
        }}
      >
        {questions.map((question) => (
          <p
            key={question.id}
            onClick={() => setSelectedQuestion(question.id)}
            style={{
              marginLeft: "2vw",
              marginTop: "1vh",
              fontSize: "1.3em",
              color:
                selectedQuestion === question.id ? "#60B84B" : "#9F9F9F",
              display: "inline-block",
              borderRadius: "50%",
              boxShadow:
                selectedQuestion === question.id
                  ? "0px 0px 10px 0px #60B84B"
                  : "0px 0px 10px 0px #00000026",
              width: "8vh",
              height: "8vh",
              textAlign: "center",
              lineHeight: "8vh",
              fontWeight: "600",
              marginRight: "1.5vw",
              cursor: "pointer",
            }}
          >
            {question.id}
          </p>
        ))}
        <p
          style={{
            color: "#9F9F9F",
            fontSize: "1.2em",
            lineHeight: "9vh",
            marginRight: "0.5vw",
          }}
        >
          Max 5 questions
        </p>
        {questions.length < 5 && (
          <span onClick={handleAddQuestion} style={{ cursor: "pointer" }}>
            <img src={plusicon} alt="plusicon" />
          </span>
        )}
      </div>

      <EachQuestion
        key={selectedQuestion} // Ensure EachQuestion component re-renders when selectedQuestion changes
        setIsAddQuestionsPopupOpen={setIsAddQuestionsPopupOpen}
        setIsQuizPublishedPopupOpen={setIsQuizPublishedPopupOpen}
      />
    </div>
  );
}

export default AddQuestionsPopup;
