import React, { useState } from "react";

function OptionBox({ onSelect, option }) {
  const [isSelected, setIsSelected] = useState(false);

  const handleClick = () => {
    setIsSelected(true);
    onSelect(option);
  };

  const handleDeselect = () => {
    setIsSelected(false);
    onSelect(option, false);
  };
  

  return (
    <div
    onClick={isSelected ? handleDeselect : handleClick}
      style={{
        border: isSelected ? "4px solid #5076FF" : "",
        backgroundColor: "#F0F0F0",
        borderRadius: "10px",
        paddingTop: "3.5vh",
        paddingBottom: "3.5vh",
        width: "18vw",
        marginTop: "10vh",
        fontWeight: "600",
        fontSize: "1.6em",
        textAlign: "center",
        cursor:"pointer",
      }}
    >
      {option}
    </div>
  );
}

export default OptionBox;
